mapper.select:
select 

from ACC_MTD_PLAN
where BILL_NO = #{billNo,jdbcType=VARCHAR}
  and PS_PERD_NO = #{psPerdNo,jdbcType=DECIMAL}

mapper.delete:
delete from ACC_MTD_PLAN
where BILL_NO = #{billNo,jdbcType=VARCHAR}

  and PS_PERD_NO = #{psPerdNo,jdbcType=DECIMAL}

delete from ACC_MTD_PLAN
where BILL_NO = #{billNo,jdbcType=VARCHAR}
  and PS_PERD_NO > #{psPerdNo,jdbcType=DECIMAL}
